import React, { useEffect, useState } from 'react';
import './App.css';

const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:3001';

function App() {
  const [quizList, setQuizList] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answer, setAnswer] = useState('');
  const [result, setResult] = useState('');
  const [correctCount, setCorrectCount] = useState(0);
  const [isQuizFinished, setIsQuizFinished] = useState(false);

useEffect(() => {
    const API_URL = `${BASE_URL}/api/quiz`;

    fetch(API_URL)
      .then(res => res.json())
      .then(data => {
        const shuffled = [...data].sort(() => Math.random() - 0.5);
        setQuizList(shuffled);
      });
  }, []);

  if (quizList.length === 0) return <div>Loading...</div>;

  const quiz = quizList[currentIndex];

  const handleSubmit = (e) => {
    e.preventDefault();
    const normalize = (str) =>
    str.trim().toLowerCase().replace(/[„Éª\s\-„ÉºÔºø]/g, '');

    const isCorrect = normalize(answer) === normalize(quiz.answer);
    
    setResult(isCorrect ? 'Ê≠£Ëß£ÔºÅ' : '‰∏çÊ≠£Ëß£...Ê≠£Ëß£„ÅØ„Äå'+ quiz.answer +'„Äç„Åß„Åô');
    if (isCorrect) {
    setCorrectCount(prev => prev + 1);
    }
  };

  const handleNext = () => {
    if (currentIndex + 1 < quizList.length) {
    setCurrentIndex(prev => prev + 1);
    setAnswer('');
    setResult('');
    } else {
    setIsQuizFinished(true); // ÊúÄÂæå„ÅÆÂïèÈ°å„ÅåÁµÇ„Çè„Å£„Åü„ÇâÁµÇ‰∫Ü„Éï„É©„Ç∞
    }
  };

  const resetQuiz = () => {
  const shuffled = [...quizList].sort(() => Math.random() - 0.5);
  setQuizList(shuffled);
  setCurrentIndex(0);
  setAnswer('');
  setResult('');
  setCorrectCount(0);
  setIsQuizFinished(false);
};

  return (
    <div className="App">
    {isQuizFinished ? (
      <div>
        <h2>„ÇØ„Ç§„Ç∫ÁµÇ‰∫ÜÔºÅ</h2>
        <p>„ÅÇ„Å™„Åü„ÅÆÊ≠£Ëß£Êï∞„ÅØ {correctCount} / {quizList.length} Âïè„Åß„Åô„ÄÇ</p>
        {correctCount === quizList.length && (
      „ÄÄ<p style={{ color: 'gold', fontWeight: 'bold' }}>üéâÁ¥†Êô¥„Çâ„Åó„ÅÑÔºÅ„ÅÇ„Å™„Åü„ÅØ„Éü„Çπ„Éâ„Éû„Çπ„Çø„Éº„Åß„ÅôÔºÅÔºÅüéâ</p>
        )}
        <button onClick={resetQuiz}>„ÇÇ„ÅÜ‰∏ÄÂ∫¶ÊåëÊà¶„Åô„Çã</button>
      </div>
    ) : (
      <>
        <h2>{quiz.question}</h2>
        <img src={`${BASE_URL}${quiz.image}`} alt="quiz" width="300" />
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={answer}
            onChange={e => setAnswer(e.target.value)}
            disabled={!!result}
          />
          <button type="submit" disabled={!!result}>ÂõûÁ≠î„Åô„Çã</button>
        </form>
        {result && (
          <>
            <p>{result}</p>
            <button onClick={handleNext}>Ê¨°„ÅÆÂïèÈ°å„Å∏</button>
          </>
        )}
        <p>Ê≠£Ëß£Êï∞: {correctCount} / {quizList.length}</p>
      </>
    )}
  </div>
  );
}

export default App;
